    setCurrentListIndex(state, value) {
      state.current_list_index = value;
    },
    setListFetched(state, value) {
      state.list_fetched = value;
    },
    resetMyLists(state) {
      Object.assign(state, INITIAL_DATA());
    }
  },
}

