export function createApp(userId = null, jwt = null) {
  // create router and store instances
  const store = createStore()

  const router = createRouter(store)

  const axios = getAxiosInstance(store, jwt);
  store.axios = axios;

  if (userId !== null) {
    store.commit('setUserId', userId);
  }

  // create the app instance, injecting both the router and the store
  const app = new Vue({
    router,
    store,
    render: h => h(App),
    data: {
      axios: axios
    }
  });

  // expose the app, the router and the store.
  return { app, router, store }
}

import jwt from 'jsonwebtoken'
import Config from './js/config-server'

import { createApp } from './app';

export default context => {
  return new Promise((resolve, reject) => {
    let jwt_string = null;
    let userId = null;
    if (context.jwt) {
      try {
        const decoded = jwt.verify(context.jwt, Config.server.jwt);
        jwt_string = context.jwt;
        userId = decoded.userid;
      }
      catch {}
    }

    const { app, router, store } = createApp(userId, jwt_string);

    // Catch the "useless" exception (this is by design)
    // https://stackoverflow.com/a/65326844
    router.push(context.url).catch(() => {});

    router.onReady(() => {
      // This `rendered` hook is called when the app has finished rendering
      context.rendered = () => {
        const matchedComponents = router.getMatchedComponents()
        // no matched routes, reject with 404
        if ( ! matchedComponents.length) {
          return reject({ code: 404 });
        }
        
        // After the app is rendered, our store is now
        // filled with the state from our components.
        // When we attach the state to the context, and the `template` option
        // is used for the renderer, the state will automatically be
        // serialized and injected into the HTML as `window.__INITIAL_STATE__`.
        context.state = store.state;
      }

      resolve(app);
    }, reject);
  })
}

import Axios from 'axios'
import Config from './config'

export default function getAxiosInstance(store, jwt) {
  let instance = Axios.create({
    baseURL: Config.app.baseURL,
    timeout: Config.app.timeout,
  });
  // Deal with 401 unauthorized
  instance.interceptors.response.use(
    response => response,
    error => {
      if (error && error.response && error.response.status === 401) {
        if (VUE_ENV === 'client') {
          store.commit('logout', false);
          store.commit('show_modal_login', true);
        }
      }
      return Promise.reject(error);
    }
  );
  // Always send the cookie. This needs to be set globally, else it won't work
  instance.defaults.withCredentials = true;

  if (jwt) {
    instance.defaults.headers.common['Cookie'] = 'jwt=' + jwt;
  }

  return instance;
}
